mapper.insert:
insert into U_ALL_REST (
  id
, name
, type
, status
, url
, source
, session
, version
, format
, remark
, creator
, created
)
values (
#{id}
,#{name}
,#{type}
,#{status}
,#{url}
,#{source}
,#{session}
,#{version}
,#{format}
,#{remark}
,#{creator}
,#{created}
)

mapper.select:
SELECT id , name , type , status , url , source , session , version , format , remark , creator , created FROM U_ALL_REST
id = #{id}
and type = #{type}
and status = #{status}



	SELECT id
		, name
		, type
		, status
		, url
		, source
		, session
		, version
		, format
		, remark
		, creator
		, created
	FROM U_ALL_REST



		id = #{id}


		and name = #{name}


		and type = #{type}


		and status = #{status}


		and source = #{source}


		and format = #{format}


		and m.creator = #{creator}


order by created desc
