mapper.update:
update K_LOGS


		logs = #{logs},


		mid = #{mid},


		creator = #{creator},


		created = #{created}


where id = #{id}

mapper.select:
SELECT id , mid , logs , creator , created FROM K_LOGS
id = #{id}
mid = #{mid}



	SELECT id
		 , mid
	     , logs
	     , creator
	     , created
	FROM K_LOGS



		and id = #{id}


		and mid = #{mid}


		and logs = #{logs}


		and creator = #{creator}


		and created = #{created}


order by created desc
