mapper.select:


	SELECT id
		, name
		, `group`
		, type
		, status
		, `read`
		, written
		, updated
		, input
		, output
		, deleted
		, retrieved
		, rejected
		, end_time endTime
		, start_time startTime
		, error
		, params
		, host
		, again
		, times
		, creator
		, created
		, modifier
        , modified
	FROM K_RESULT



		name = #{name}


		and `group` = #{group}


		and type = #{type}


		and status = #{status}


		and `read` = #{read}


		and written = #{written}


		and updated = #{updated}


		and input = #{input}


		and output = #{output}


		and deleted = #{deleted}


		and end_time = #{endTime}


		and startTime = #{startTime}


		and retrieved = #{retrieved}


		and rejected = #{rejected}


		and error = #{error}


		and creator = #{creator}


		and again = #{again}


		and created = #{created}


		and modified = #{modified}


order by created desc
