mapper.insert:
insert into K_SPOON (
	id
	, rid
	, type
	, status
	, method
	, dir
	, logs
	, async
	, params
	, remark
	, creator
	, created
	, modifier
	, modified
)
values (
	#{id}
	,#{rid}
	,#{type}
	,#{status}
	,#{method}
	,#{dir}
	,#{logs}
	,#{async}
	,#{params}
	,#{remark}
	,#{creator}
	,#{created}
	,#{modifier}
	,#{modified}
)

mapper.select:


	SELECT id
	    , rid
		, type
		, status
		, method
		, dir
		, logs
		, async
		, params
		, remark
		, creator
		, created
		, modifier
		, modified
	FROM K_SPOON



		id = #{id}


		and rid = #{rid}


		and type = #{type}


		and status = #{status}


		and method = #{method}


		and dir = #{dir}


		and logs = #{logs}


		and creator = #{creator}


		and created = #{created}


		and modifier = #{modifier}


		and modified = #{modified}


order by created desc
